INSERT INTO `bnccs` (`id`, `codigo`, `objetivo_habilidade`, `nivel_de_ensino`, `aprendizagem_desenvolvimento`, `unidade_tematica`, `disciplina_id`, `assinatura_id`, `team_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(993, 'EF05CI06', 'Selecionar argumentos que justifiquem por que os sistemas digestório e respiratório são considerados corresponsáveis pelo processo de nutrição do organismo, com base na identificação das funções desses sistemas.', 'Ensino Fundamental', '', 'Vida e evolução', '4', NULL, NULL, '2021-12-02 06:42:06', '2021-12-02 06:42:06', NULL),
(994, 'EF05CI07', 'Justificar a relação entre o funcionamento do sistema circulatório, a distribuição dos nutrientes pelo organismo e a eliminação dos resíduos produzidos.', 'Ensino Fundamental', '', 'Vida e evolução', '4', NULL, NULL, '2021-12-02 06:42:06', '2021-12-02 06:42:06', NULL),
(995, 'EF05CI08', 'Organizar um cardápio equilibrado com base nas características dos grupos alimentares (nutrientes e calorias) e nas necessidades individuais (atividades realizadas, idade, sexo etc.) para a manutenção da saúde do organismo.', 'Ensino Fundamental', '', 'Vida e evolução', '4', NULL, NULL, '2021-12-02 06:42:06', '2021-12-02 06:42:06', NULL),
(1031, 'EF08CI01', 'Identificar e classificar diferentes fontes (renováveis e não renováveis) e tipos de energia utilizados em residências, comunidades ou cidades.', 'Ensino Fundamental', '', 'Matéria e energia', '4', NULL, NULL, '2021-12-02 06:42:06', '2021-12-02 06:42:06', NULL),
(1032, 'EF08CI02', 'Construir circuitos elétricos com pilha/bateria, fios e lâmpada ou outros dispositivos e compará-los a circuitos elétricos residenciais.', 'Ensino Fundamental', '', 'Matéria e energia', '4', NULL, NULL, '2021-12-02 06:42:06', '2021-12-02 06:42:06', NULL),
(1033, 'EF08CI03', 'Classificar equipamentos elétricos residenciais (chuveiro, ferro, lâmpadas, TV, rádio, geladeira etc.) de acordo com o tipo de transformação de energia (da energia elétrica para a térmica, luminosa, sonora e mecânica, por exemplo).', 'Ensino Fundamental', '', 'Matéria e energia', '4', NULL, NULL, '2021-12-02 06:42:06', '2021-12-02 06:42:06', NULL),
(1034, 'EF08CI04', 'Calcular o consumo de eletrodomésticos a p4ir dos dados de potência (descritos no próprio equipamento) e tempo médio de uso para avaliar o impacto de cada equipamento no consumo doméstico mensal.', 'Ensino Fundamental', '', 'Matéria e energia', '4', NULL, NULL, '2021-12-02 06:42:06', '2021-12-02 06:42:06', NULL),
(1035, 'EF08CI05', 'Propor ações coletivas para otimizar o uso de energia elétrica em sua escola e/ou comunidade, com base na seleção de equipamentos segundo critérios de sustentabilidade (consumo de energia e efi1 energética) e hábitos de consumo responsável.', 'Ensino Fundamental', '', 'Matéria e energia', '4', NULL, NULL, '2021-12-02 06:42:06', '2021-12-02 06:42:06', NULL),
(1036, 'EF08CI06', 'Discutir e avaliar usinas de geração de energia elétrica (termelétricas, hidrelétricas, eólicas etc.), suas semelhanças e diferenças, seus impactos socioambientais, e como essa energia chega e é usada em sua cidade, comunidade, casa ou escola.', 'Ensino Fundamental', '', 'Matéria e energia', '4', NULL, NULL, '2021-12-02 06:42:06', '2021-12-02 06:42:06', NULL),
(1037, 'EF08CI07', 'Comparar diferentes processos reprodutivos em plantas e animais em relação aos mecanismos adaptativos e evolutivos.', 'Ensino Fundamental', '', 'Vida e evolução', '4', NULL, NULL, '2021-12-02 06:42:06', '2021-12-02 06:42:06', NULL),
(1038, 'EF08CI08', 'Analisar e explicar as transformações que ocorrem na puberdade considerando a atuação dos hormônios sexuais e do sistema nervoso.', 'Ensino Fundamental', '', 'Vida e evolução', '4', NULL, NULL, '2021-12-02 06:42:06', '2021-12-02 06:42:06', NULL),
(1039, 'EF08CI09', 'Comparar o modo de ação e a eficácia dos diversos métodos contraceptivos e justificar a necessidade de comp4ilhar a responsabilidade na escolha e na utilização do método mais adequado à prevenção da gravidez precoce e indesejada e de Doenças Sexualmente Transmissíveis (DST).', 'Ensino Fundamental', '', 'Vida e evolução', '4', NULL, NULL, '2021-12-02 06:42:06', '2021-12-02 06:42:06', NULL),
(1040, 'EF08CI10', 'Identificar os principais sintomas, modos de transmissão e tratamento de algumas DST (com ênfase na AIDS), e discutir estratégias e métodos de prevenção.', 'Ensino Fundamental', '', 'Vida e evolução', '4', NULL, NULL, '2021-12-02 06:42:06', '2021-12-02 06:42:06', NULL),
(1041, 'EF08CI11', 'Selecionar argumentos que evidenciem as múltiplas dimensões da sexualidade humana (biológica, sociocultural, afetiva e ética).', 'Ensino Fundamental', '', 'Vida e evolução', '4', NULL, NULL, '2021-12-02 06:42:06', '2021-12-02 06:42:06', NULL),
(1295, 'EF08HI21', 'Identificar e analisar as políticas oficiais com relação ao indígena durante o Império.', 'Ensino Fundamental', '', 'O Brasil no século XIX', '4', NULL, NULL, '2021-12-02 06:42:06', '2021-12-02 06:42:06', NULL),
